import React, { useEffect, useRef } from 'react';

export default function BarChartDate({ nbFab3Mois }) {
  const svgRef = useRef();
  const width = 600;
  const height = 500;
  const margin = { top: 20, right: 30, bottom: 40, left: 40 };
  useEffect(() => {
    // D3.js code to draw the bar chart
    const svg = d3.select(svgRef.current);
    if (!svg.empty()) {
      svg.attr('width', width).attr('height', height);

      // Thiết lập scale cho trục x
      const xScale = d3
        .scaleBand()
        .domain(nbFab3Mois.map((d) => Object.keys(d)[0]))
        .range([margin.left, width - margin.right])
        .padding(0.1);

      // Thiết lập scale cho trục y
      const yScale = d3
        .scaleLinear()
        .domain([0, d3.max(nbFab3Mois, (d) => Object.values(d)[0])])
        .nice()
        .range([height - margin.bottom, margin.top]);

      svg
        .selectAll('rect')
        .data(nbFab3Mois)
        .enter()
        .append('rect')
        .attr('x', (d) => xScale(Object.keys(d)[0]))
        .attr('y', (d) => yScale(Object.values(d)[0]))
        .attr('width', xScale.bandwidth())
        .attr(
          'height',
          (d) => height - margin.bottom - yScale(Object.values(d)[0])
        )
        .attr('fill', 'steelblue');

      // Thêm nhãn giá trị bên trong mỗi thanh
      svg
        .selectAll('text')
        .data(nbFab3Mois)
        .enter()
        .append('text')
        .text((d) => Object.values(d)[0])
        .attr('x', (d, i) => xScale(Object.keys(d)[0]) + xScale.bandwidth() / 2) // Sử dụng chỉ số i để xác định vị trí trên trục x
        .attr('y', (d) => yScale(Object.values(d)[0]) - 5)
        .attr('text-anchor', 'middle')
        .attr('font-size', '14px')
        .attr('fill', 'gray ');

      // Dựng trục x
      svg
        .append('g')
        .attr('transform', `translate(0,${height - margin.bottom})`)
        .call(d3.axisBottom(xScale));

      // Dựng trục y
      svg
        .append('g')
        .attr('transform', `translate(${margin.left},0)`)
        .call(d3.axisLeft(yScale).ticks(5));

      // Đặt tên trục và tiêu đề
      svg
        .append('text')
        .attr('x', width / 2)
        .attr('y', height)
        .text('Key');

      svg
        .append('text')
        .attr('x', -height / 2)
        .attr('y', 10)
        .attr('transform', 'rotate(-90)')
        .text('Value');
    }
  }, [nbFab3Mois]);

  return (
    <>
      <div>BarChartDate</div>
      <svg ref={svgRef}></svg>
    </>
  );
}
