import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BarChart = ({ data }) => {
  const svgRef = useRef();
  const width = 600;
  const height = 500;
  const margin = { top: 20, right: 30, bottom: 40, left: 40 };
  useEffect(() => {
    // D3.js code to draw the bar chart
    const svg = d3.select(svgRef.current);
    if (!svg.empty()) {
      svg.attr('width', width).attr('height', height);

      // Thiết lập scale cho trục x
      const xScale = d3
        .scaleBand()
        .domain(data.map((d) => Object.keys(d)[0]))
        .range([margin.left, width - margin.right])
        .padding(0.1);

      // Thiết lập scale cho trục y
      const yScale = d3
        .scaleLinear()
        .domain([0, d3.max(data, (d) => Object.values(d)[0])])
        .nice()
        .range([height - margin.bottom, margin.top]);

      svg
        .selectAll('rect')
        .data(data)
        .enter()
        .append('rect')
        .attr('x', (d) => xScale(Object.keys(d)[0]))
        .attr('y', (d) => yScale(Object.values(d)[0]))
        .attr('width', xScale.bandwidth())
        .attr(
          'height',
          (d) => height - margin.bottom - yScale(Object.values(d)[0])
        )
        .attr('fill', 'steelblue');
      // Dựng trục x
      svg
        .append('g')
        .attr('transform', `translate(0,${height - margin.bottom})`)
        .call(d3.axisBottom(xScale));

      // Dựng trục y
      svg
        .append('g')
        .attr('transform', `translate(${margin.left},0)`)
        .call(d3.axisLeft(yScale).ticks(5));

      // Đặt tên trục và tiêu đề
      // svg
      //   .append('text')
      //   .attr('x', width / 2)
      //   .attr('y', height)
      //   .text('Key');

      // svg
      //   .append('text')
      //   .attr('x', -height / 2)
      //   .attr('y', 10)
      //   .attr('transform', 'rotate(-90)')
      //   .text('Value');
      svg
        .selectAll('text')
        .data(data)
        .enter()
        .append('text')
        .text((d) => d.value)
        .attr('x', (d) => xScale(d.label) + xScale.bandwidth() / 2)
        .attr('y', (d) => yScale(d.value) - 5)
        .attr('text-anchor', 'middle')
        .attr('font-size', '14px')
        .attr('fill', 'white');
    }
  }, [data]);
  return <svg ref={svgRef}></svg>;
};

export default BarChart;
