import { React, useRef, useState, useEffect } from 'react';
import '../asset/health.scss';
import * as d3 from 'd3';
import BarChartView from './BarChart';
function Health({
  fabData,
  magData,
  prodData,
  prodByFab,
  topMagasinByFab,
  topMagasinByProd,
}) {
  const svgRef = useRef(null);

  const dateString = [
    'Janvier 2022',
    'Février 2022',
    'Mars 2022',
    'Avril 2022',
    'Mai 2022',
    'Juin 2022',
    'Juillet 2022',
    'Août 2022',
    'Septembre 2022',
    'Octobre 2022',
    'Novembre 2022',
    'Decembre 2022',
    'Janvier 2023',
  ];
  const optionTabs = ['Statistiques', 'Top 10 magasins', 'Moyenne de produit'];
  const currentTab = useRef('Statistiques');
  const handleTabClick = (tabName) => {
    currentTab.current = tabName;
    console.log(currentTab.current);
  };

  return (
    <>
      <div className="tabs">
        {optionTabs.map((item) => (
          <div className="tab">
            <div
              key={item}
              onClick={() => handleTabClick(item)}
              className={currentTab.current === item ? 'active' : ''}
            >
              {item}
            </div>
          </div>
        ))}
      </div>
      {currentTab.current === optionTabs[0] ? (
        <>
          <div className="box-container">
            <div className="small-box">
              <h1>Nombre de fabricants</h1>
              <p>{fabData}</p>
            </div>

            <div className="small-box">
              <h1>Nombre de produits</h1>
              <p>{prodData}</p>
            </div>

            <div className="small-box">
              <h1>Nombre de magasins</h1>
              <p>{magData}</p>
            </div>
          </div>
          <h3>Moyenne</h3>
          <div className="box-container">
            <div className="small-box">
              <h1>Moyenne de produits par fabricants</h1>
              <p>{prodByFab}</p>
            </div>
          </div>
        </>
      ) : (
        ''
      )}
      {currentTab.current === optionTabs[1] ? (
        <div>
          <h3>Top 10 magasins</h3>
          <div class="box-container">
            <BarChartView topMagasinByFab={topMagasinByFab} />
          </div>
        </div>
      ) : (
        ''
      )}
    </>
  );
}

export default Health;
