import { React, useRef, useState, useEffect } from 'react';
import '../asset/health.scss';
import * as d3 from 'd3';

function Health({ catID, data, mag, annee }) {
  const [nombreFab, setNombreFab] = useState(0);
  const [nombreProd, setNombreProd] = useState(0);
  const [nombreMag, setNombreMag] = useState(0);
  const [moyenneProduit, setMoyenneProduit] = useState(0);
  const [topFab, setTopFab] = useState([]);
  const svgRef = useRef(null);

  const fabData = [];
  const prodData = [];
  const magData = [];
  const prodByFab = [];

  const dateString = [
    'Janvier 2022',
    'Février 2022',
    'Mars 2022',
    'Avril 2022',
    'Mai 2022',
    'Juin 2022',
    'Juillet 2022',
    'Août 2022',
    'Septembre 2022',
    'Octobre 2022',
    'Novembre 2022',
    'Decembre 2022',
    'Janvier 2023',
  ];
  const optionTabs = ['Statistiques', 'Top 10 magasins', 'Moyenne de produit'];
  const currentTab = 'Statistiques';

  useEffect(() => {
    handleData(5);
    BarChart();
  }, []);

  const handleData = (category) => {
    let topMagasin = [];
    let topMagasinByFab = [];
    let topMagasinByProd = [];

    data.forEach((item) => {
      if (Number(item.catID) === category) {
        if (!fabData.includes(item.fabID)) {
          // Questin 1.1
          fabData.push(item.fabID);
        } else {
          // • Question 1.2.
          if (!prodByFab.includes(item.prodID)) {
            prodByFab.push(item.prodID);
          }
        }
        // • Question 1.1.
        if (!prodData.includes(item.prodID)) {
          prodData.push(item.prodID);
        }
        // • Question 1.3.

        if (item.magID && mag) {
          if (!magData.includes(item.magID)) {
            magData.push(item.magID);
          } else {
            if (topMagasin[item.magID]) {
              let dataF = topMagasin[item.magID].fab;
              let dataP = topMagasin[item.magID].prod;
              if (!dataF.includes(item.fabID)) {
                dataF.push(item.fabID);
              }
              if (!dataP.includes(item.prodID)) {
                dataP.push(item.prodID);
              }
              topMagasin[item.magID] = { fab: dataF, prod: dataP };
            } else {
              topMagasin[item.magID] = {
                fab: [item.fabID],
                prod: [item.prodID],
              };
            }
          }
        }
      }
    });
    setNombreFab(fabData.length);
    setNombreProd(prodData.length);
    setNombreMag(magData.length);
    setMoyenneProduit(
      Math.round((prodByFab.length / fabData.length) * 100) / 100
    );
    if (mag) {
      // FOR question 3

      // Sorted top magasion
      const toFabMagasin = Object.keys(topMagasin).map((item) => {
        const lenF = topMagasin[item].fab.length;
        const obj = {};
        obj[item] = lenF;
        return obj;
      });

      const toProdMagasin = Object.keys(topMagasin).map((item) => {
        const lenP = topMagasin[item].prod.length;
        const obj = {};
        obj[item] = lenP;
        return obj;
      });

      const fabSorted = toFabMagasin.sort((a, b) => {
        return Number(Object.values(b).pop()) - Number(Object.values(a).pop());
      });
      // await sleep(100);
      const prodSorted = toProdMagasin.sort(
        (a, b) =>
          Number(Object.values(b).pop()) - Number(Object.values(a).pop())
      );

      topMagasinByFab = fabSorted.slice(0, 10);
      topMagasinByProd = prodSorted.slice(0, 10);
      setTopFab(topMagasinByFab);
      console.log(topFab);
      // Question 1.4
      // Pose fabID = 506, catID = 5, magasin top 10

      // handleFab(checkFabID.value, category, topMagasinByFab, topMagasinByProd);
    }
  };
  console.log(topFab);
  const BarChart = () => {
    const width = 400;
    const height = 300;
    let svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);
    // Xác định domain cho trục x và trục y
    const xScale = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => Object.values(d)[0])])
      .range([0, width]);

    const yScale = d3
      .scaleBand()
      .domain(data.map((d, i) => i))
      .range([0, height])
      .padding(0.1);
    svg
      .selectAll('rect')
      .data(data)
      .enter()
      .append('rect')
      .attr('x', 0)
      .attr('y', (d, i) => yScale(i))
      .attr('width', (d) => xScale(Object.values(d)[0]))
      .attr('height', yScale.bandwidth())
      .attr('fill', 'steelblue');
  };
  const xAxis = d3.axisBottom(xScale);
  svg.append('g').attr('transform', `translate(0, ${height})`).call(xAxis);

  const yAxis = d3.axisLeft(yScale);
  svg.append('g').call(yAxis);
  return (
    <>
      <div className="tabs">
        {optionTabs.map((item) => (
          <div key={item} className="tab">
            {item}
          </div>
        ))}
      </div>
      <div className="box-container">
        <div className="small-box">
          <h1>Nombre de fabricants</h1>
          <p>{nombreFab}</p>
        </div>

        <div className="small-box">
          <h1>Nombre de produits</h1>
          <p>{nombreProd}</p>
        </div>

        <div className="small-box">
          <h1>Nombre de magasins</h1>
          <p>{nombreMag}</p>
        </div>
      </div>
      <h3>Moyenne</h3>
      <div className="box-container">
        <div className="small-box">
          <h1>Moyenne de produits par fabricants</h1>
          <p>{moyenneProduit}</p>
        </div>
      </div>
      <div>
        <h3>Top 10 magasins</h3>
        <div class="box-container">
          <svg ref={svgRef} id="my-svg"></svg>
        </div>
      </div>
    </>
  );
}

export default Health;
