        .selectAll('rect')
        .data(chartFab)
        .enter()
        .append('rect')
        .attr('x', (d, i) => xScale(dateString[i]))
        .attr('y', (d) => yScale(d))
        .attr('width', xScale.bandwidth())
        .attr('height', (d) => height - yScale(d))
        .attr('fill', 'steelblue');

