import { useState, useEffect } from 'react';
import './App.scss';
import { useFile } from './store/useFile';
import Health from './component/Health';
import Papa from 'papaparse';
import FileInput from 'react-file-reader';

function App() {
  const [dataFile, setDataFile] = useState([]);
  const [isLoading] = useState(true);
  const file = useFile();
  const [categoryData, setCategoryData] = useState(null);
  const [selectedValue, setSelectedValue] = useState(''); // State để lưu giá trị đã chọn từ select
  const category = [];

  const anneeOptions = ['Tous les années', '2022', '2023'];
  const annee = 'Tous les annees';
  const dataToShow =
    '/Users/user/Desktop/etl/my-app/src/data/pointsDeVente-tous';

  const fileOptions = [
    {
      label: 'Points De Vente',
    },
    {
      label: 'Produit',
    },
  ];

  useEffect(() => {
    const fetchData = async () => {
      await handleFile(); // Đợi cho handleFile() hoàn thành
      handleCategory(); // Sau đó thực hiện handleCategory()
    };

    fetchData();
  }, []);

  // useEffect(() => {
  //   // Đường dẫn đến tệp CSV (điều này cần phải được điều chỉnh cho đúng đường dẫn của bạn)
  //   const csvFilePath =
  //     '/Users/user/Desktop/etl/my-app/src/data/pointsDeVente-tous.csv';

  //   // Sử dụng PapaParse để đọc tệp CSV
  //   Papa.parse(csvFilePath, {
  //     header: true, // Đảm bảo tệp CSV có hàng tiêu đề
  //     dynamicTyping: true, // Tự động xác định kiểu dữ liệu
  //     skipEmptyLines: true, // Bỏ qua các dòng trống
  //     complete: (result) => {
  //       const data = result.data;
  //       console.log(data); // Dữ liệu từ tệp CSV
  //     },
  //   });
  // }, []);

  const handleFile = async () => {
    try {
      const getData = await file.getFileData();
      setDataFile(getData);
    } catch (e) {
      console.log(e);
    }
  };

  const handleCategory = () => {
    dataFile.forEach((item) => {
      if (!category.includes(item.catID)) category.push(item.catID);
    });
    category.sort((a, b) => a - b);
  };

  const handleSelectChange = (event) => {
    const selectedOption = event.target.value; // Lấy giá trị đã chọn
    console.log(selectedOption);
    setSelectedValue(selectedOption); // Cập nhật state với giá trị đã chọn
  };

  return (
    <>
      <h2>Notification du score de santé d’un fabricant sur le marché</h2>

      <div className="box-select-container">
        <div className="box">
          <div className="box-select">
            <h2 className="title">Choisir les données</h2>
            <select className="select">
              {fileOptions.map((item) => (
                <option key={item.label} value={item.label}>
                  {item.label}
                </option>
              ))}
            </select>
          </div>
          <div className="box-select">
            <h2 className="title">Choisir la catégorie</h2>
            <select className="select" onChange={handleSelectChange}>
              {console.log(category)}
              {category.map((item, index) => (
                <option key={index + 1} value={index + 1}>
                  {index + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="box-select">
            <h2 className="title">Choisir l'années</h2>
            <select className="select">
              {anneeOptions.map((item) => (
                <option key={item} value={item}>
                  {item}
                </option>
              ))}
            </select>
          </div>
        </div>
        <div>
          {/* <Health
            mag="true"
            data={dataFile}
            annee={annee}
            catID={selectedValue}
          /> */}
        </div>
      </div>
    </>
  );
}

export default App;
