import { useState, useEffect } from 'react';
import './App.scss';
import { useFile } from './store/useFile';
import Health from './component/Health';
import Papa from 'papaparse';
import dataCSV from './data/pointsDeVente-tous.csv';
import FileInput from 'react-file-reader';

function App() {
  const [category, SetCategory] = useState(5);
  const [data, setData] = useState([]);
  const [isLoading] = useState(true);
  const file = useFile();
  const [categoryData, setCategoryData] = useState([]);

  const anneeOptions = ['Tous les années', '2022', '2023'];
  const annee = 'Tous les annees';
  const dataToShow =
    '/Users/user/Desktop/etl/my-app/src/data/pointsDeVente-tous.csv';

  const fileOptions = [
    {
      label: 'Points De Vente',
    },
    {
      label: 'Produit',
    },
  ];

  useEffect(() => {
    // const fetchData = async () => {
    //   const newData = await handleFile();
    //   handleCategory(newData);
    // };
    // fetchData();
  }, []);
  // useEffect(() => {
  //   // Đường dẫn đến tệp CSV (điều này cần phải được điều chỉnh cho đúng đường dẫn của bạn)
  //   const csvFilePath =
  //     '/Users/user/Desktop/etl/my-app/src/data/pointsDeVente-tous.csv';

  //   // Sử dụng PapaParse để đọc tệp CSV
  //   Papa.parse(csvFilePath, {
  //     header: true, // Đảm bảo tệp CSV có hàng tiêu đề
  //     dynamicTyping: true, // Tự động xác định kiểu dữ liệu
  //     skipEmptyLines: true, // Bỏ qua các dòng trống
  //     complete: (result) => {
  //       const data = result.data;
  //       console.log(data); // Dữ liệu từ tệp CSV
  //     },
  //   });
  // }, []);

  // const handleFile = async () => {
  //   try {
  //     const getData = await file.getFileData();
  //     setData(getData);

  //     return getData;
  //   } catch (e) {
  //     console.log(e);
  //   }
  // };
  const handleCSVFile = (files) => {
    const file = files[0]; // Lấy tệp đã chọn

    if (file) {
      Papa.parse(file, {
        header: true, // Đảm bảo rằng tệp CSV có hàng tiêu đề
        dynamicTyping: true, // Tự động xác định kiểu dữ liệu
        skipEmptyLines: true, // Bỏ qua các dòng trống
        complete: (result) => {
          const data = result.data;
          console.log(data); // Dữ liệu từ tệp CSV
        },
      });
    }
  };

  const handleCategory = (newData) => {
    const categoryData = [];
    newData.forEach((item) => {
      if (!categoryData.includes(item.catID)) categoryData.push(item.catID);
    });
    categoryData.sort((a, b) => a - b);
    setCategoryData(categoryData);
  };

  return (
    <>
      <h2>Notification du score de santé d’un fabricant sur le marché</h2>
      <div>
        <FileInput as="text" accept=".csv" onChange={handleCSVFile}>
          <button>Chọn tệp CSV</button>
        </FileInput>
      </div>
      <div className="box-select-container">
        <div className="box">
          <div className="box-select">
            <h2 className="title">Choisir les données</h2>
            <select className="select">
              {fileOptions.map((item) => (
                <option key={item.label} value={item.label}>
                  {item.label}
                </option>
              ))}
            </select>
          </div>
          <div className="box-select">
            <h2 className="title">Choisir la catégorie</h2>
            <select className="select">
              {categoryData.map((item, index) => (
                <option key={item} value={index + 1}>
                  {index + 1}
                </option>
              ))}
            </select>
          </div>
          <div className="box-select">
            <h2 className="title">Choisir l'années</h2>
            <select className="select">
              {anneeOptions.map((item) => (
                <option key={item} value={item}>
                  {item}
                </option>
              ))}
            </select>
          </div>
        </div>
        <div>
          {/* <Health mag="true" data={data} annee={annee} catID={category} /> */}
        </div>
      </div>
    </>
  );
}

export default App;
