import { React, useRef, useState } from 'react';

function Health({ catID, data, mag, annee }) {
  const [nombreFab, setNombreFab] = useState(0);
  const [nombreProd, setNombreProd] = useState(0);
  const [nombreMag, setNombreMag] = useState(0);
  const fabData = [];
  const prodData = [];
  const magData = [];
  const prodByFab = [];

  const dateString = [
    'Janvier 2022',
    'Février 2022',
    'Mars 2022',
    'Avril 2022',
    'Mai 2022',
    'Juin 2022',
    'Juillet 2022',
    'Août 2022',
    'Septembre 2022',
    'Octobre 2022',
    'Novembre 2022',
    'Decembre 2022',
    'Janvier 2023',
  ];
  const optionTabs = ['Statistiques', 'Top 10 magasins', 'Moyenne de produit'];
  const currentTab = 'Statistiques';

  const handleData = (category) => {
    data.forEach((item) => {
      if (Number(item.catID) === category) {
        if (!fabData.includes(item.fabID)) {
          // Questin 1.1
          fabData.push(item.fabID);
        } else {
          // • Question 1.2.
          if (!prodByFab.includes(item.prodID)) {
            prodByFab.push(item.prodID);
          }
        }
        // • Question 1.1.
        if (!prodData.includes(item.prodID)) {
          prodData.push(item.prodID);
        }
        if (item.magID && mag) {
          if (!magData.includes(item.magID)) {
            magData.push(item.magID);
          }
        }
      }
    });
    setNombreFab(fabData.length);
    setNombreProd(prodData.length);
    setNombreMag(magData.length);
    console.log('56', nombreFab.current);
  };

  return (
    <div className="box-container">
      <div className="small-box">
        <h1>Nombre de fabricants</h1>
        <p>{nombreFab}</p>
      </div>

      <div className="small-box">
        <h1>Nombre de produits</h1>
        <p>{nombreProd}</p>
      </div>

      <div className="small-box">
        <h1>Nombre de magasins</h1>
        <p>{nombreMag}</p>
      </div>
    </div>
  );
}

export default Health;
