import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BarChart = ({ data }) => {
  const svgRef = useRef();
  console.log(data);
  const width = 600;
  const height = 500;
  useEffect(() => {
    // D3.js code to draw the bar chart
    const svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    const Buffer = 25;

    //color
    const color = d3.scaleThreshold().domain([Math.round(d3.mean(data))]);
  }, [data]);

  return <svg ref={svgRef}></svg>;
};

export default BarChart;
