import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BarChart = ({ data }) => {
  const svgRef = useRef();
  console.log(data);
  const width = 600;
  const height = 400;
  useEffect(() => {
    // D3.js code to draw the bar chart
    const svg = d3
      .select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    const xScale = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => Object.values(d)[0])])
      .range([0, width]);

    const yScale = d3
      .scaleBand()
      .domain(data.map((d, i) => i))
      .range([0, height])
      .padding(0.1);

    svg
      .selectAll('rect')
      .data(data)
      .enter()
      .append('rect')
      .append('rect')
      .attr('x', 0)
      .attr('y', (d, i) => yScale(i))
      .attr('width', (d) => xScale(Object.values(d)[0]))
      .attr('height', yScale.bandwidth())
      .attr('fill', 'steelblue');

    // Thêm trục x và trục y
    const xAxis = d3.axisBottom(xScale);
    svg.append('g').attr('transform', `translate(0, ${height})`).call(xAxis);

    const yAxis = d3.axisLeft(yScale);
    svg.append('g').call(yAxis);
  }, [data]);

  return <svg ref={svgRef}></svg>;
};

export default BarChart;
