import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BarChart = ({ data }) => {
  const svgRef = useRef();
  const width = 600;
  const height = 500;
  useEffect(() => {
    // D3.js code to draw the bar chart
    const svg = d3.select(svgRef.current);
    if (!svg.empty()) {
      svg
        .attr('width', svg.node().clientWidth)
        .attr('height', svg.node().clientHeight);

      const BUFFER = 25;
      //color
      const color = d3
        .scaleThreshold()
        .domain([Math.round(d3.mean(data))])
        .range(['#AED6F1', '#2874A6']);
      const y_axis_scale = d3
        .scaleLinear()
        .domain([0, Math.max(...data)])
        .range([BUFFER, height - BUFFER]);
      const y_axis = d3
        .axisLeft(y_axis_scale)
        .tickSizeOuter(0)
        .tickSizeInner(3)
        .ticks(12);
      const y_axis_g = svg.append('g').attr('id', 'yAxisG');
      //RENDER Y AXIS
      y_axis(y_axis_g);
      // Transform y axis group
      y_axis_g.attr('transform', `translate(${BUFFER},0)`);
    }
  }, [data]);
  return <svg ref={svgRef}></svg>;
};

export default BarChart;
