import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import Chart from 'chart.js/auto';

const BarChartMoyenne = ({
  moyenneProdTop1,
  moyenneProdTop2,
  chartProd,
  chartFab,
