      .scaleBand()
      .domain(dateString)
      .range([margin.left, width - 750])
      .padding(0.1);

    const yScale = d3
      .scaleLinear()
      .domain([0, d3.max(chartFab)])
      .nice()
      .range([height - margin.bottom, margin.top]);

    // Vẽ các thanh bar
    svg
      .selectAll('rect')
      .data(chartFab)
      .enter()
      .append('rect')
      .attr('x', (d, i) => xScale(dateString[i]))
      .attr('y', (d) => yScale(d))
      .attr('width', xScale.bandwidth())
      .attr('height', (d) => height - margin.bottom - yScale(d))
      .attr('fill', 'steelblue');

    // Hiển thị giá trị trên mỗi thanh bar
    svg
      .selectAll('text')
      .data(chartFab)
      .enter()
      .append('text')
      .attr(
        'x',
        (d) => xScale(dateString[chartFab.indexOf(d)]) + xScale.bandwidth() / 2
      )
      .attr('y', (d) => yScale(d) - 5) // Điều chỉnh vị trí của giá trị
      .attr('text-anchor', 'middle')
      .attr('font-size', '10px')
      .attr('fill', 'white')
      .text((d) => d);

    // Vẽ trục x và trục y
    svg
      .append('g')
      .attr('transform', `translate(0,${height - margin.bottom})`)
      .call(d3.axisBottom(xScale));

    svg
      .append('g')
      .attr('transform', `translate(${margin.left},0)`)
      .call(d3.axisLeft(yScale));
  }, [chartFab, dateString]);
